mapper.select:
SELECT m1.*, m2.id AS id2, m2.url AS url2, m2.path AS path2, m2.component AS component2, m2.`name` AS name2, m2.iconCls AS iconCls2, m2.keepAlive AS keepAlive2, m2.requireAuth AS requireAuth2, m2.parentId AS parentId2, m2.enabled AS enabled2 FROM t_menu m1, t_menu m2, t_menu_role mr, t_admin_role ar WHERE m1.id = m2.parentId AND m2.id = mr.mid AND mr.rid = ar.rid AND m2.`enabled` = TRUE
AND ar.adminId = #{id}
ORDER BY m1.id
